import Mitt from './util/Mitt.js'

/**
 * ì–‘ë°©í–¥ ë°ì´í„° ì „ì†¡ì„ ìœ„í•œ ì¸í„°í˜ì´ìŠ¤. RTCSocketì„ ì´ìš©í•´ ë°ì´í„°ë¥¼ ì „ì†¡í•©ë‹ˆë‹¤.
 */
export default class Channel extends Mitt {
  /**
   * ì£¼ì˜: ì´ ìƒì„±ìëŠ” RTCEngine ë‚´ë¶€ì—ì„œë§Œ í˜¸ì¶œë˜ì–´ì•¼ í•©ë‹ˆë‹¤.
   * @param {RTCSocket} socket ë°ì´í„° ì „ì†¡ì— ì‚¬ìš©í•  RTCSocket
   * @param {RTCEngine} engine ì´ ì±„ë„ì„ ìƒì„±í•œ ì—”ì§„
   */
  constructor (socket, engine) {
    super()

    this.engine = engine
    this.socket = socket
    this.socket.on('data', data => this.emit('message', data))
    this.label = this.socket.dataChannel.label
    this.filesSent = 0

    this.socket.on('__file-transaction', label => this.receiveTransaction(label))
  }

  /**
   * ìƒëŒ€ê°€ íŒŒì¼ì„ ë³´ë‚´ê¸° ìœ„í•´ íŠ¸ë Œì ì…˜ì„ ë§Œë“¤ì—ˆì„ë•Œ íŒŒì¼ì„ ë°›ê¸° ìœ„í•œ íŠ¸ë Œì ì…˜ì„ ìƒì„±í•˜ê³  `transaction` ì´ë²¤íŠ¸ë¡œ ì•Œë¦½ë‹ˆë‹¤.
   * @deprecated
   * @param {string} label íŠ¸ë Œì ì…˜ì˜ ì‹ë³„ì
   */
  async receiveTransaction (label) {
    const transaction = await this.engine.readable(label)
    this.emit('transaction', transaction)
  }

  /**
   * ì±„ë„ì„ í†µí•´ì„œ ë°ì´í„°ë¥¼ ì „ì†¡í•©ë‹ˆë‹¤. `File` ë°ì´í„°ë¥¼ ë°›ìœ¼ë©´ ìƒˆë¡œìš´ íŠ¸ë Œì ì…˜ì„ ë§Œë“¤ê³  ê·¸ê±¸ í†µí•´ íŒŒì¼ì˜ ë°ì´í„°ë¥¼ ì „ì†¡í•©ë‹ˆë‹¤.
   * ë°›ëŠ”ìª½ì—ì„œëŠ” `transaction` ì´ë²¤íŠ¸ë¥¼ í†µí•´ íŒŒì¼ì„ ë°›ì„ ìˆ˜ ìˆìŠµë‹ˆë‹¤.
   * @deprecated
   * @param {*} data ì „ì†¡í•  ë°ì´í„°. `JSON.stringify()`ë¡œ JSON ë¬¸ìì—´ë¡œ ë°”ê¿€ ìˆ˜ ìˆê±°ë‚˜ `ArrayBuffer`ì˜ íƒ€ì…ì€ ê·¸ëƒ¥ ì „ì†¡í•˜ê³ , `File`ì¸ ê²½ìš° ìƒˆë¡œìš´ íŠ¸ë Œì ì…˜ì„ ë§Œë“­ë‹ˆë‹¤.
   * @returns {Promise<Transaction|void>} íŒŒì¼ ì „ì†¡ì„ ìœ„í•œ íŠ¸ë Œì ì…˜ ë˜ëŠ” ë°ì´í„°ê°€ ì„±ê³µì ìœ¼ë¡œ ì „ì†¡ë˜ë©´ resolveí•˜ëŠ” promise
   */
  async send (data) {
    if (data instanceof File) {
      const file = data
      const label = `file - ${this.filesSent++}`
      this.socket.writeEvent('__file-transaction', label)

      const transaction = await this.engine.writable(label, {
        name: file.name,
        size: file.size
      })

      file.stream().pipeTo(transaction.stream)
      return transaction
    }

    return this.socket.write(data)
  }
}

class FixedSizeBuffer {
  constructor (size) {
    this.size = size
    this.source = new Uint8Array(size)
    this.bufferedAmount = 0
    this.desiredAmount = size
  }

  consume (source) {
    const sourceBytes = source.length
    let consumedBytes

    if (sourceBytes > this.desiredAmount) {
      this.source.set(source.subarray(0, this.desiredAmount), this.bufferedAmount)
      consumedBytes = this.desiredAmount
    } else {
      this.source.set(source, this.bufferedAmount)
      consumedBytes = sourceBytes
    }

    this.bufferedAmount += consumedBytes
    this.desiredAmount -= consumedBytes

    return consumedBytes
  }

  flush () {
    return this.source.slice(0, this.bufferedAmount)
  }

  get full () {
    return this.desiredAmount === 0
  }
}

export default class ChunkProducer {
  constructor (size) {
    this.size = size
    this.buffer = new FixedSizeBuffer(this.size)
  }

  /**
     *
     * @param {Uint8Array} chunk
     * @param {*} controller
     */
  transform (chunk, controller) {
    if (!(chunk instanceof Uint8Array)) {
      controller.enqueue(chunk)
    }

    let processedBytes = 0
    let subarray = chunk
    while (processedBytes < chunk.length) {
      processedBytes += this.buffer.consume(subarray)
      if (this.buffer.full) {
        controller.enqueue(this.buffer.source)
        // TODO: FixedSizeBuffer ì¬ì‚¬ìš©?
        this.buffer = new FixedSizeBuffer(this.size)
        subarray = chunk.subarray(processedBytes)
      }
    }
  }

  flush (controller) {
    // ë§ˆì§€ë§‰ chunkëŠ” ì‚¬ì´ì¦ˆê°€ ì¼ì •í•˜ì§€ ì•ŠìŒ
    const chunk = this.buffer.flush()
    if (chunk) {
      controller.enqueue(chunk)
    }
  }
}

import Mitt from './util/Mitt.js'
import { ObservableEntry } from './util/ObservableEntry.js'
import once from './util/once.js'
import createLogger from './util/createLogger.js'

// RTCDataChannelì€ ì‹¤ì œ ë²„í¼ ì‚¬ì´ì¦ˆë¥¼ ë³´ì—¬ì£¼ì§€ ì•Šìœ¼ë‚˜ ì‚¬ì´ì¦ˆë¥¼ ë„˜ê²Œ ë°ì´í„°ê°€ ë“¤ì–´ê°€ë©´ ì±„ë„ì´ í„°ì ¸ë²„ë¦¼
// ë”°ë¼ì„œ 10MBë¥¼ ë°ì´í„° ì±„ë„ì˜ ë²„í¼ ì‚¬ì´ì¦ˆë¡œ ìƒê°
// ë˜ writable ìŠ¤íŠ¸ë¦¼ì˜ ë²„í¼ ì‚¬ì´ì¦ˆë¡œë„ ì‚¬ìš©
const DATA_CHANNEL_BUFFER_SIZE = 10 * 1024 * 1024 // 10MB

export default class RTCSocket extends Mitt {
  // options - received boolean
  constructor (dataChannel, options = {}) {
    super()

    /** @type {RTCDataChannel} */
    this.dataChannel = dataChannel
    this.dataChannel.binaryType = 'arraybuffer'
    this.dataChannel.addEventListener('message', ({ data }) => this.recvData(data))
    this.dataChannel.addEventListener('close', () => {
      this.emit('close')

      if (this.closed) return

      // close() í˜¸ì¶œ ì´ì™¸ì˜ ì´ìœ ë¡œ ë‹«íŒ ê²½ìš°
      this.ready.set(false)
      this.logger.log('ğŸš« ìƒëŒ€ë°©ì— ì˜í•´ì„œ ì†Œì¼“ì´ ë‹«í˜”ìŠµë‹ˆë‹¤.')
    })
    this.label = this.dataChannel.label

    // ì•ì˜ ë©”ì‹œì§€ê°€ ë²„í¼ ì‚¬ì´ì¦ˆ ë¬¸ì œë¡œ ì¸í•´ ëŒ€ê¸° ì¤‘ì´ë¼ë©´ ì´ê²Œ trueë¡œ ì„¤ì •ë¨
    // ë’¤ì˜ ë©”ì‹œì§€ëŠ” ì´ ì†ì„±ì´ falseê°€ ë˜ë©´ ì²˜ë¦¬ë¨
    this.ready = new ObservableEntry(true)
    this.closed = false

    this.logger = createLogger(`Socket:${this.label}`)

    if (options.received) {
      this.writeEvent('__received')
    }
  }

  async writeEvent (eventName, payload) {
    this.logger.debug(`ì»¤ìŠ¤í…€ ì´ë²¤íŠ¸ ${eventName} ì „ì†¡ì„ ìš”ì²­í–ˆìŠµë‹ˆë‹¤. í˜ì´ë¡œë“œ:`, payload)
    return this.write({
      _channelEngineCustomEvent: true,
      event: eventName,
      payload
    })
  }

  recvData (msg) {
    let data
    if (msg instanceof ArrayBuffer) {
      data = msg
      this.logger.debug('ì´ì§„ ë°ì´í„°ë¥¼ ì „ì†¡ë°›ì•˜ìŠµë‹ˆë‹¤. ì‚¬ì´ì¦ˆ:', msg.byteLength)
    } else {
      data = JSON.parse(msg)
      this.logger.debug('ë°ì´í„°ë¥¼ ì „ì†¡ë°›ì•˜ìŠµë‹ˆë‹¤.', data)
    }

    // ì»¤ìŠ¤í…€ ì´ë²¤íŠ¸ ì²˜ë¦¬
    if (typeof data === 'object') {
      if ('_channelEngineCustomEvent' in data) {
        this.emit(data.event, data.payload)
        return
      }
    }

    this.emit('data', data)
  }

  /**
     *
     * @param {object|string|number|ArrayBuffer} data
     */
  async write (data) {
    let msg
    if (data instanceof ArrayBuffer) {
      // ë°ì´í„°ì±„ë„ ë²„í¼ ê´€ë¦¬
      if (data.byteLength > DATA_CHANNEL_BUFFER_SIZE) {
        throw new Error('ë°ì´í„° í¬ê¸°ê°€ ë°ì´í„° ì±„ë„ì˜ ë²„í¼ ì‚¬ì´ì¦ˆë³´ë‹¤ ì»¤ì„œ ì „ì†¡í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤.')
      }

      if (data.byteLength + this.dataChannel.bufferedAmount > DATA_CHANNEL_BUFFER_SIZE) {
        this.ready.set(false)
        this.dataChannel.bufferedAmountLowThreshold = DATA_CHANNEL_BUFFER_SIZE - data.byteLength
        this.logger.debug(`ë²„í¼ì— ê³µê°„ì´ í™•ë³´ë˜ê¸°ë¥¼ ëŒ€ê¸°í•˜ëŠ” ì¤‘ì…ë‹ˆë‹¤. ë©”ì‹œì§€ ì‚¬ì´ì¦ˆ: ${data.byteLength}, ë²„í¼ ê³µê°„: ${DATA_CHANNEL_BUFFER_SIZE - this.dataChannel.bufferedAmount}`)
        await once(this.dataChannel, 'bufferedamountlow')
        this.logger.debug('ë²„í¼ì— ê³µê°„ì´ í™•ë³´ë˜ì—ˆìŠµë‹ˆë‹¤.')
        this.dataChannel.bufferedAmountLowThreshold = 0
      }

      msg = data
      this.logger.debug('ì´ì§„ ë°ì´í„°ë¥¼ ì „ì†¡í–ˆìŠµë‹ˆë‹¤. ì‚¬ì´ì¦ˆ:', msg.byteLength)
    } else {
      msg = JSON.stringify(data)
      this.logger.debug('ë°ì´í„°ë¥¼ ì „ì†¡í–ˆìŠµë‹ˆë‹¤. ', data)
    }

    if (this.dataChannel.readyState !== 'open') {
      await once(this.dataChannel, 'open')
    }

    this.dataChannel.send(msg)
    this.ready.set(true)
  }

  close () {
    this.dataChannel.close()
    this.ready.set(false)
    this.closed = true
    this.logger.log('ğŸš« close() ë©”ì†Œë“œê°€ í˜¸ì¶œë˜ì„œ ì†Œì¼“ì„ ë‹«ì•˜ìŠµë‹ˆë‹¤.')
  }
}

import Transaction from './Transaction.js'

export default class ReadableTransaction extends Transaction {
  /**
   * íŠ¸ë Œì ì…˜ì„ ë§Œë“­ë‹ˆë‹¤.
   * @param {RTCSocket} socket ë°ì´í„° ì „ì†¡ì— ì‚¬ìš©í•  RTCSocket
   * @param {object} [metadata] ìƒëŒ€ì—ê²Œ ì „ì†¡í•  ë©”íƒ€ë°ì´í„°. íŠ¸ë Œì ì…˜ì´ ë§Œë“¤ì–´ì§„ í›„ `metadata` ì†ì„±ìœ¼ë¡œ ì½ì„ ìˆ˜ ìˆìŠµë‹ˆë‹¤. Progress Trackingì„ ì‚¬ìš©í•˜ë ¤ë©´ `size` ì†ì„±ì´ í•„ìš”í•©ë‹ˆë‹¤. ê·¸ ì´ì™¸ì˜ ì†ì„±ì€ ì„ì˜ë¡œ ì¶”ê°€í•  ìˆ˜ ìˆìŠµë‹ˆë‹¤.
   * @param {number} [metadata.size] ë°”ì´íŠ¸ë¡œ ë‚˜íƒ€ë‚¸ íŠ¸ë Œì ì…˜ì˜ í¬ê¸°.
   */
  constructor (socket, metadata = {}) {
    super(socket, metadata)

    this.bufferFullInformed = false
    this.aborted = false
    this.canceled = false
    this.cancelReason = null

    this.stream = new ReadableStream({
      start: controller => {
        const isDone = () => this.processed.get() === metadata.size

        socket.on('data', data => {
          if (!(data instanceof ArrayBuffer)) return

          controller.enqueue(data)
          this.processed.set(this.processed.get() + data.byteLength)

          if (controller.desiredSize < 0 && !this.bufferFullInformed) {
            this.bufferFullInformed = true
            socket.writeEvent('buffer-full')
            this.logger.debug('ReadableStream ë²„í¼ê°€ ê°€ë“ ì°¼ìŠµë‹ˆë‹¤. ì „ì†¡ ì¼ì‹œì¤‘ì§€ë¥¼ ìš”ì²­í–ˆìŠµë‹ˆë‹¤.')
          }

          if (isDone()) {
            socket.close()
            controller.close()
            this.done.set(true)
            this.logger.log('âš¡ ì „ì†¡ì´ ì™„ë£Œë˜ì—ˆìŠµë‹ˆë‹¤.')
          }
        })

        socket.once('close', () => {
          this.stopReport()
          if (this.aborted || isDone()) return

          if (this.canceled) {
            if (this.cancelReason instanceof Error) {
              controller.error(this.cancelReason)
            } else {
              controller.error('íŠ¸ë Œì ì…˜ì´ Cancelë˜ì—ˆìŠµë‹ˆë‹¤. ì´ìœ : ' + this.cancelReason)
            }
          }

          controller.error(new Error('ì˜ˆìƒì¹˜ ëª»í•˜ê²Œ ì†Œì¼“ì´ ë‹«í˜”ìŠµë‹ˆë‹¤.'))
        })

        socket.once('abort', errMsg => {
          this.aborted = true
          socket.close()
          controller.error(new Error('ìƒëŒ€ë°©ì´ íŠ¸ë Œì ì…˜ì˜ WritableStreamì„ Abortí–ˆìŠµë‹ˆë‹¤.' + errMsg))
        })

        // writerì¸¡ì—ì„œ ì¼ì‹œì •ì§€/ì¬ê°œë¬ì„ë•Œ
        socket.on('pause', () => {
          this.logger.debug('ìƒëŒ€ë°©ì´ íŠ¸ë Œì ì…˜ ì¼ì‹œì¤‘ì§€ë¥¼ ìš”ì²­í–ˆìŠµë‹ˆë‹¤.')
          super.pause()
        })
        socket.on('resume', () => {
          this.logger.debug('ìƒëŒ€ë°©ì´ íŠ¸ë Œì ì…˜ ì¬ì‹œì‘ì„ ìš”ì²­í–ˆìŠµë‹ˆë‹¤.')
          super.resume()
        })
      },
      pull: () => {
        if (!this.bufferFullInformed) return

        socket.writeEvent('pull')
        this.bufferFullInformed = false
        this.logger.debug('ReadableStream ë²„í¼ê°€ ê³µê°„ì´ í™•ë³´ë˜ì–´ì„œ ì „ì†¡ ì¬ê°œë¥¼ ìš”ì²­í–ˆìŠµë‹ˆë‹¤.')
      },
      cancel: reason => {
        this.cancel(reason)
      }
    }, new ByteLengthQueuingStrategy({ highWaterMark: 10 * 1024 * 1024 /* 10 MB */ }))
  }

  cancel (reason = '') {
    this.canceled = true
    this.cancelReason = reason

    if (reason instanceof Error) {
      this.socket.writeEvent('cancel', reason.message)
    } else {
      this.socket.writeEvent('cancel', reason)
    }

    this.logger.warn('íŠ¸ë Œì ì…˜ì˜ ReadableStreamì´ Cancelë˜ì—ˆìŠµë‹ˆë‹¤. ì´ìœ : ', reason)
  }

  stop () {
    this.cancel('ë°›ëŠ” ìª½ì—ì„œ stop() ë©”ì†Œë“œë¥¼ í˜¸ì¶œí–ˆìŠµë‹ˆë‹¤.')
  }

  pause () {
    super.pause()
    this.socket.writeEvent('pause')
  }

  resume () {
    super.resume()
    this.socket.writeEvent('resume')
  }
}

import Mitt from './util/Mitt.js'
import { ObservableEntry, wait } from './util/ObservableEntry.js'
import progressTracker from './util/eta.js'
import prettyBytes from './util/prettyBytes.js'
import createLogger from './util/createLogger.js'

// /** @typedef {import('./RTCSocket.js').default} RTCSocket */

/**
 * ë‹¨ë°©í–¥ ë°ì´í„° ì „ì†¡ì„ ìœ„í•œ ì¸í„°í˜ì´ìŠ¤. í•œ í”¼ì–´ì—ì„œ ë‹¤ë¥¸ í”¼ì–´ë¡œ íŒŒì¼ê³¼ ê°™ì€ ë°ì´í„°ë¥¼ ì „ì†¡í•  ë•Œ ì‚¬ìš©ë©ë‹ˆë‹¤.
 * `stream` ì†ì„±ì„ í†µí•´ì„œ ì½ê±°ë‚˜ ì“¸ ìˆ˜ ìˆëŠ” ìŠ¤íŠ¸ë¦¼ì„ ì´ìš©í•  ìˆ˜ ìˆìŠµë‹ˆë‹¤.
 * ë˜, ë©”íƒ€ë°ì´í„° ì „ì†¡ / ì „ì†¡ ì»¨íŠ¸ë¡¤(ì¼ì‹œì •ì§€, ì¬ê°œ, ì¤‘ë‹¨) / ì „ì†¡ ì†ë„ ë° ì§„í–‰ë¥  ì¶”ì ë“±ì˜ ê¸°ëŠ¥ì„ ì œê³µí•©ë‹ˆë‹¤.
 * ì´ í´ë˜ìŠ¤ëŠ” ë³´ë‚´ëŠ” ìª½ê³¼ ë°›ëŠ” ìª½ì—ì„œ ê³µí†µì ìœ¼ë¡œ ì‚¬ìš©ë˜ëŠ” ê¸°ëŠ¥ì„ êµ¬í˜„í•œ ë² ì´ìŠ¤ë¡œ ì‹¤ì œ íŒŒì¼ ì „ì†¡ì— ê´€ë ¨ëœ ì½”ë“œëŠ” ReadableTransaction.jsì™€ WritableTransaction.jsì— ìˆìŠµë‹ˆë‹¤.
 */
export default class Transaction extends Mitt {
  /**
   * íŠ¸ë Œì ì…˜ì„ ë§Œë“­ë‹ˆë‹¤.
   * @param {RTCSocket} socket ë°ì´í„° ì „ì†¡ì— ì‚¬ìš©í•  RTCSocket
   * @param {object} [metadata] ìƒëŒ€ì—ê²Œ ì „ì†¡í•  ë©”íƒ€ë°ì´í„°. íŠ¸ë Œì ì…˜ì´ ë§Œë“¤ì–´ì§„ í›„ `metadata` ì†ì„±ìœ¼ë¡œ ì½ì„ ìˆ˜ ìˆìŠµë‹ˆë‹¤. Progress Trackingì„ ì‚¬ìš©í•˜ë ¤ë©´ `size` ì†ì„±ì´ í•„ìš”í•©ë‹ˆë‹¤. ê·¸ ì´ì™¸ì˜ ì†ì„±ì€ ì„ì˜ë¡œ ì¶”ê°€í•  ìˆ˜ ìˆìŠµë‹ˆë‹¤.
   * @param {number} [metadata.size] ë°”ì´íŠ¸ë¡œ ë‚˜íƒ€ë‚¸ íŠ¸ë Œì ì…˜ì˜ í¬ê¸°.
   */
  constructor (socket, metadata = {}) {
    super()

    /** @type {RTCSocket} */
    this.socket = socket
    this.metadata = metadata
    this.label = this.socket.label
    this.paused = new ObservableEntry(false)
    this.done = new ObservableEntry(false)

    // ì „ì†¡ ìƒíƒœ íŠ¸ë ˆí‚¹
    this.lastPausedTimestamp = 0
    this.pausedMilliSeconds = 0
    this.processed = new ObservableEntry(0) // byte or length

    this.timeout = NaN
    this.logger = createLogger(`Transaction:${this.label}`)

    this.initProgressTracking()
  }

  async initProgressTracking () {
    // sizeê°€ ì„¤ì •ë˜ì–´ ìˆì§€ ì•Šë‹¤ë©´ progress trackingì„ ì‚¬ìš©í•˜ì§€ ì•ŠìŒ
    if (this.metadata === undefined) {
      this.logger.warn('ë©”íƒ€ë°ì´í„°ê°€ undefinedì…ë‹ˆë‹¤. Progress Tracking ê¸°ëŠ¥ì´ ë™ì‘í•˜ì§€ ì•ŠìŠµë‹ˆë‹¤.')
      return
    }
    if (typeof this.metadata.size !== 'number') {
      this.logger.warn('ë©”íƒ€ë°ì´í„°ì˜ size í•„ë“œê°€ ìˆ«ìê°€ ì•„ë‹ˆê±°ë‚˜ ì •ì˜ë˜ì§€ ì•Šì•˜ìŠµë‹ˆë‹¤. Progress Tracking ê¸°ëŠ¥ì´ ë™ì‘í•˜ì§€ ì•ŠìŠµë‹ˆë‹¤.')
      return
    }

    await wait(this.processed).toBeChanged()

    // transaction writer ìª½ì—ì„  ì²˜ìŒ ì‹œì‘ë¶€í„° ì†ë„ ì¸¡ì •ì‹œ
    // ë°ì´í„° ì±„ë„ì˜ ë²„í¼ê°€ ë‹¤ ì°¨ê¸° ì „ì´ë¼ ì†ë„ê°€ ë¹„ì •ìƒì ìœ¼ë¡œ ë¹ ë¥´ê²Œ ì¸¡ì •ë˜ë¯€ë¡œ 1ì´ˆ í›„ ì‹œì‘
    await new Promise(resolve => setTimeout(resolve, 1000))

    if (this.done.get()) return

    const processed = this.processed.get()
    this.progressTracker = progressTracker({
      min: processed,
      max: this.metadata.size + processed,
      historyTimeConstant: 10
    })

    this.timeout = setInterval(() => {
      if (this.paused.get()) return

      const timestamp = Date.now() - this.pausedMilliSeconds
      this.progressTracker.report(this.processed.get(), timestamp)

      const report = {
        processed: this.processed.get(),
        progress: this.progress,
        eta: this.eta,
        speed: this.speed
      }

      this.logger.debug('í†µê³„ ë°ì´í„°ë¥¼ ìƒì„±í–ˆìŠµë‹ˆë‹¤.', report)
      this.emit('report', report)
    }, 500)
  }

  get eta () {
    if (!this.progressTracker) {
      return NaN
    }

    if (this.paused.get()) {
      return Math.round(this.progressTracker.estimate(this.lastPausedTimestamp))
    }

    if (this.processed.get() === this.metadata.size) {
      return 0
    }

    return Math.round(this.progressTracker.estimate(Date.now() - this.pausedMilliSeconds)) // ê²°ê³¼ëŠ” ì´ˆ
  }

  get progress () {
    return this.processed.get() / this.metadata.size
  }

  get speed () {
    if (!this.progressTracker) {
      return 'NaNB/s'
    }

    if (this.paused.get()) {
      return '0B/s'
    }

    return prettyBytes(this.progressTracker.rate()) + '/s'
  }

  pause () {
    this.logger.debug('íŠ¸ë Œì ì…˜ì´ ì¼ì‹œì¤‘ì§€ë˜ì—ˆìŠµë‹ˆë‹¤.')
    this.paused.set(true)
    this.lastPausedTimestamp = Date.now()
  }

  resume () {
