      }
    })

    this.abortController = new AbortController()

    // ì´ë ‡ê²Œ í•˜ë©´ pipeTo(transactionWriter.stream)ì²˜ëŸ¼ ì‚¬ìš© ê°€ëŠ¥
    // ë’¤ì˜ catch()ë¬¸ì€ abortì‹œ ì—ëŸ¬ê°€ ë‘êµ°ë°ì—ì„œ ë°œìƒí•˜ëŠ”ë°(ì—¬ê¸°ì™€ this.streamì— pipeTo í•œ ë¶€ë¶„)
    // ì—¬ê¸°ì„œ ì—ëŸ¬ê°€ ë°œìƒí•˜ì§€ ì•Šê²Œ í•˜ê¸° ìœ„í•œ ê²ƒì„
    // eslint-disable-next-line no-undef
    const chunkingStream = new TransformStream(new ChunkProducer(CHUNK_SIZE))
    chunkingStream.readable.pipeTo(writable, { signal: this.abortController.signal }).catch(() => {})
