   */
  connected (engine) {}

  /**
   * @abstract
   * @param {RTCEngine} engine ì—”ì§„ ì¸ìŠ¤í„´ìŠ¤
   */
  disconnected (engine) {}

  /**
   * @abstract
   * @param {RTCEngine} engine ì—”ì§„ ì¸ìŠ¤í„´ìŠ¤
   */
  failed (engine) {}

  /**
   * @abstract
   * @param {RTCEngine} engine ì—”ì§„ ì¸ìŠ¤í„´ìŠ¤
   */
  close (engine) {}
}

import SignalerBase from './Base.js'
