    // broadcast channelë¡œë¶€í„° ë©”ì‹œì§€ë¥¼ ë°›ëŠ” í—¨ë“¤ëŸ¬
    this.bc.addEventListener('message', evt => {
      const msg = JSON.parse(evt.data)

