  receiveHeartbeat (sender) {
    if (this.sender === null) {
      // ë©”ì‹œì§€ë¥¼ ì²˜ìŒ ë°›ì•˜ì„ ê²½ìš°
      this.sender = sender
    } else if (sender !== this.sender) {
      // ë³´ë‚¸ ì‚¬ëŒì˜ idê°€ ì¼ì¹˜í•˜ì§€ ì•Šì„ ê²½ìš°
      return
