    this.ready.set(true)
    this.heartbeatTimeoutTimer.reset()
  }

  /**
   * ìƒëŒ€ì—ê²Œ ë©”ì‹œì§€ë¥¼ ì „ì†¡í•©ë‹ˆë‹¤.
   * @param {*} msg ì „ì†¡í•  ë©”ì‹œì§€.
   */
  send (msg) {
    const data = JSON.stringify(msg)
    this.emit('outgoing-msg', data)

    // postMessageëŠ” JSONìœ¼ë¡œ ë°”ê¾¸ì§€ ì•Šì•„ë„ ì˜¤ë¸Œì íŠ¸ë¥¼ ë³´ë‚¼ ìˆ˜ ìˆì§€ë§Œ
    // RTCSessionDescriptionì„ ë³´ë‚´ë©´ ì˜¤ë¥˜ê°€ ë‚¨, ë”°ë¼ì„œ JSONìœ¼ë¡œ ë°”ê¿”ì¤˜ì•¼ í•¨
    this.bc.postMessage(data)
  }

  /**
   * close í›…. Broadcast Channelì„ ë‹«ìŠµë‹ˆë‹¤.
   */
  close () {
    this.heartbeatTimeoutTimer.clear()
