export default class ObservableQueue extends Queue {
  async * pushes () {
    let lastTail = 0

    while (true) {
      await once(this, 'push')

      for (let i = lastTail; i < this.tail; i++) {
        yield this.data[i]
      }
      lastTail = this.tail
    }
  }
}

import Mitt from './Mitt.js'

export default class Queue extends Mitt {
  constructor () {
    super()
    this.head = 0
    this.tail = 0
    this.data = {}
  }

  push (val) {
    const index = this.tail++
    this.data[index] = val
    this.emit('push', val)
  }

  pop () {
    const index = this.head++
    const val = this.data[index]
    delete this.data[index]
    return val
  }

  get size () {
    return this.tail - this.head
  }

  * [Symbol.iterator] () {
    for (let i = this.head; i < this.tail; i++) {
      yield this.data[i]
    }
  }

  flush () {
    this.head = 0
    this.tail = 0
    this.data = {}
  }
}

