let inGroup = false
let disabled = false

const methodToColorMap = {
  debug: '#7f8c8d', // Gray
  log: '#3498db', // Blue
  warn: '#f39c12', // Yellow
  error: '#c0392b', // Red
  groupCollapsed: '#3498db', // Blue
  groupEnd: null // No colored prefix on groupEnd
}

/**
 * ë±ƒì§€ì™€ í•¨ê»˜ ë¡œê·¸ë¥¼ ì°ìŠµë‹ˆë‹¤.
 * @param {'debug'|'log'|'warn'|'error'|'groupCollapsed'|'groupEnd'} method ì‚¬ìš©í•  ë¡œê·¸ í•¨ìˆ˜ì˜ ì´ë¦„
 * @param {string} src ë¡œê·¸ë¥¼ ì°ëŠ” ì£¼ì²´.
 * @param  {...any} args ì°ì„ ë¡œê·¸ì˜ ë‚´ìš©ë“¤.
 */
function log (method, src, ...args) {
  if (disabled) return

  // prefix ë±ƒì§€ ë§Œë“¤ê¸°
  const prefixStyle = [
    `background: ${methodToColorMap[method]}`,
    'border-radius: 0.5em',
    'color: white',
    'font-weight: bold',
    'padding: 2px 0.5em'
  ]

  // groupì•ˆì— ìˆì„ë•ŒëŠ” ë±ƒì§€ë¥¼ ìˆ¨ê²¨ì•¼ í•¨
  const logPrefix = inGroup ? [] : [`%c${src}`, prefixStyle.join(';')]

  // ë¡œê·¸ ì°ê¸°
  console[method](...logPrefix, ...args)

  if (method === 'groupCollapsed') {
    inGroup = true
  } else if (method === 'groupEnd') {
    inGroup = false
  }
}

export default function createLogger (src) {
  return {
    debug: (...args) => log('debug', src, ...args),
    log: (...args) => log('log', src, ...args),
    warn: (...args) => log('warn', src, ...args),
    error: (...args) => log('error', src, ...args),
    groupCollapsed: (...args) => log('groupCollapsed', src, ...args),
    groupEnd: (...args) => log('groupEnd', src, ...args)
  }
}

export function disableLog () {
  disabled = true
}

